package main

import (
	"fmt"
	"log"
)

// https://leetcode.com/problems/longest-palindromic-substring/

func expandPalindrome(s string, i,j int16, max string) string {
	var res string

	for i >= 0 && j < int16(len(s)) && s[i] == s[j] {
		res = s[i:j+1]
		i--
		j++
	}

	if len(max) < len(res) {
		return res
	}
	return max
}

func longestPalindrome(s string) string {
	var res string

	if len(s) == 1 {
		return s
	}

	for i := int16(0); i < int16(len(s)); i++ {
		res = expandPalindrome(s, i, i, res)
		res = expandPalindrome(s, i, i+1, res)
	}

	return res
}

func main() {
	for _, x := range []string{
		"",
		"11111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111",
		"abacad", "addc", "cacacacac", "c", "aaabaaaa", "cbbd", "babad"} {
		log.Println("FINDING PALINGDROMS:", x, len(x))
		res := longestPalindrome(x)
		log.Println("ANSWER:", res, len(res))
		fmt.Println()
	}
}